INSERT INTO `wp_posts` VALUES (7414,1,'2022-02-21 09:35:41','2022-02-21 09:35:41','[vc_row][vc_column][vc_column_text]\r\n<h4>STRATÉGIE DE COMMUNICATION</h4>\r\n&nbsp;\r\n\r\nCommunication 2022\r\n\r\n&nbsp;\r\n<h5>PROBLÉMATIQUE :</h5>\r\nComment rendre visible en digital la nouvelle marque de bières Espigal du groupe Arterris, acteur économique agricole majeur dans le sud de la France qui fédère plus de 25 000 agricultrices et agriculteurs ?\r\n\r\n&nbsp;\r\n<h5>LA SOLUTION :</h5>\r\nRéalisation d’un site responsive qui porte les valeurs de savoir-faire et d’une production occitane.\r\n\r\n&nbsp;\r\n<h5>CONCEPT CRÉATIF :</h5>\r\nWebdesign permettant une immersion dans l\'univers frais et artisanal de la bière\r\n\r\n[/vc_column_text][/vc_column][/vc_row]','Espigal','Une production occitane','publish','closed','closed','','espigal','','','2022-04-22 12:46:08','2022-04-22 12:46:08','',0,'https://www.agence-solution.fr/?post_type=portfolio-item&#038;p=7414',0,'portfolio-item','',0);
